import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Form from 'react-bootstrap/Form';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import css from '../assets/Css/App.css'
import { useState, useEffect } from 'react';
import NavigationService from '../services/navigation.service';
import { Link } from 'react-router-dom';


function IndexNavbar() {
    const [menu, setMenu] = useState([])

    useEffect(() => {
        NavigationService.GetNavMenu()
            .then(res => {
                setMenu(res)
                console.log(res)
            })
    });

    return (
        <Navbar expand="lg" className="bg-body-tertiary" fixed="top" >
            <Container fluid>
                <Navbar.Brand href="/" ><img src="https://ik.imagekit.io/2q7cgnqzi/GI-Logo-new-e1685611674392.webp?updatedAt=1709890243463" alt="" height={50} width={50} /></Navbar.Brand>
                <Navbar.Toggle aria-controls="navbarScroll" />
                <Navbar.Collapse id="navbarScroll">
                    <Nav
                        className="me-auto my-2 my-lg-0"
                        style={{ maxHeight: '100px' }}
                        navbarScroll
                    >
                        {menu.map((menu) => {
                            if (menu.submenu) {
                                return <>
                                    <NavDropdown title="Products" id="navbarScrollingDropdown">

                                        {menu.submenu.map((product) => {
                                            return (<NavDropdown.Item className='spacenav' tag={Link} to={`${product.path}`} state={{ menu: product }}>{product.name}</NavDropdown.Item>)
                                            return (<NavDropdown.Item><Link className='spacenav' tag={Link} to={`${product.path}`} state={{ menu: product }}>{product.name} </Link> </NavDropdown.Item>)
                                        })}

                                    </NavDropdown>
                                </>
                            } else {

                                return <Nav.Link tag={Link} to={`${menu.path}`} state={{ menu: menu }}>{menu.name}</Nav.Link>
                                return <Nav.Link><Link className='navlink' to={`${menu.path}`} state={{ menu: menu }}>{menu.name}</Link></Nav.Link>
                            }
                        })}

                        {/* <NavDropdown title="Our Products" id="navbarScrollingDropdown">
                            <NavDropdown.Item href="#action3">Action</NavDropdown.Item>
                            <NavDropdown.Item href="#action4">
                                Another action
                            </NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="#action5">
                                Something else here
                            </NavDropdown.Item>
                        </NavDropdown>
                        <Nav.Link href="#action2">Blogs</Nav.Link>
                        <Nav.Link href="#action2">Exhibitions</Nav.Link>
                        <Nav.Link to="/contactus">Contact Us</Nav.Link>
                        <Nav.Link href="#action2">Privacy Policy</Nav.Link> */}

                    </Nav>

                    <Form className="d-flex">
                        {/* <Form.Control
                            type="search"
                            placeholder="Search"xnccnjcjncasd   
                            className="me-2"
                            aria-label="Search"
                        /> */}

                        <img src="https://ik.imagekit.io/2q7cgnqzi/icons8-linkedin.svg?updatedAt=1710240114493" alt="" height={40} width={40} style={{ marginRight: '20px' }} />
                        <img src="https://ik.imagekit.io/2q7cgnqzi/icons8-facebook.svg?updatedAt=1710239929085" alt="" height={40} width={40} style={{ marginRight: '20px' }} />
                        <Button variant="outline-success" className='navbtn' style={{ borderRadius: '15px' }}>
                            <img src="https://uxwing.com/wp-content/themes/uxwing/download/communication-chat-call/phone-call-icon.png" alt="" width={25} />
                            +91 8800900862 </Button>
                        <Button variant="outline-success" className='navbtn' style={{ borderRadius: '15px', marginLeft: '3px' }}>Contact</Button>
                    </Form>
                </Navbar.Collapse>
            </Container>
        </Navbar>
    );
}

export default IndexNavbar;
