import { React, useState } from 'react'
import IndexNavbar from '../Components/IndexNavbar'
import { Container, Row, Col, } from 'reactstrap'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';

function Home() {
    const [formdata, setformdata] = useState({})
    const handlechange = (e) => {
        setformdata({ ...formdata, [e.target.id]: e.target.value });
    };

    // const submitdata = () => {

    //     alert("Data Submitted Successfully")


    // }
    return (
        <>
            <IndexNavbar />

            <Container className='homepage' >
                <Row>
                    <Col md='6' style={{ boxSizing: 'border-box', display: 'flex', flexFlow: 'column', justifyContent: 'center', alignItems: 'center', height: '70vh' }} >
                        <h1>Welcome</h1>
                        <h2>in gaurav's blog</h2>
                    </Col>
                    <Col md='6' style={{ boxSizing: 'border-box', display: 'flex', flexFlow: 'column', justifyContent: 'center', alignItems: 'center', height: '70vh' }}   >

                        <Form onSubmit={submitdata} >
         
                                <Form.Label>Enter your name</Form.Label>
                                <Form.Control type="text" id='name' placeholder="Enter Name" onChange={handlechange} />

       


                                <Form.Label>Email address</Form.Label>
                                <Form.Control type="email" id='email' placeholder="Enter email" onChange={handlechange} />



                                <Form.Label>Password</Form.Label>
                                <Form.Control type="password" placeholder="Password" onChange={handlechange} />
          


                            <Button variant="primary" type="submit"  >
                                Submit
                            </Button>
                        </Form>
                    </Col>

                </Row>
            </Container>

        </>
    )
}

export default Home
